import { useRef, useContext } from 'react';
import AuthContext from '../../store/auth-context';
import classes from './ProfileForm.module.css';

// const apiKey= AIzaSyAuuWxHAZ1zdrtnRKKwIpzekRFMrLAIN58;
const ProfileForm = () => {
  const ctx = useContext(AuthContext);

  const newPasswordInputRef = useRef();
  const handleChangePassword=(e)=>{
    e.preventDefault();
    const newPassword = newPasswordInputRef.current.value;
    // add validation
    fetch('https://identitytoolkit.googleapis.com/v1/accounts:update?key=AIzaSyAuuWxHAZ1zdrtnRKKwIpzekRFMrLAIN58', {
      method: 'POST',
      body: JSON.stringify({
        idToken: ctx.token,
        password: newPassword,
        returnSecureToken: false
      }),
      headers: {
        'Content-Type': 'application/json'
      }
    }).then(res => {
      // assumption: Always succeeds!


    });
  };


  return (
    <form className={classes.form} onSubmit={handleChangePassword}>
      <div className={classes.control}>
        <label htmlFor='new-password'>New Password</label>
        <input type='password' id='new-password' ref={newPasswordInputRef}/>
      </div>
      <div className={classes.action}>
        <button>Change Password</button>
      </div>
    </form>
  );
}

export default ProfileForm;
